package com.syntax.class04;

public class WeekClassSchedule {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * 
		 * 
		 */
		int day=7;
		
		if(day==1) {
			System.out.println("Today is monday we have no class");
			
		}else if(day==2) {
			System.out.println("Today is Tueasday we have Manual class");
			
		}else if(day==3) {
			System.out.println("Today is Wednesday and we manual class again");
			
		}else if(day==4) {
			System.out.println("Today is Thursday and we have review calss");
			
		}else if(day==5) {
			System.out.println("Today is Fraiday and we have no class");
			
		}else if(day==6) {
			System.out.println("Today is Saturday and i miss java class");
			
		}else if(day==7) {
			System.out.println("Today is Sunday and I did a lot of coding");
			
		}else {
			System.out.println("This is invalid day of a week");
		
		}

	}

}
